mapper.update:
UPDATE article
title = #{title}, `body` = #{body}, updateDate = NOW()
WHERE id = #{id}

mapper.select:
SELECT a.*
FROM article a
WHERE id = #{id}

SELECT a.*, m.nickname
AS extra__writer
FROM article a
INNER JOIN `member` m ON a.memberId =
m.id
WHERE a.boardId = #{boardId}
ORDER BY a.id DESC
LIMIT #{limit}
OFFSET #{offset}

SELECT COUNT(*)
FROM article
WHERE boardId = #{boardId}
